mapper.select:
SELECT COUNT(*) AS subscriberCount
FROM subscribe
WHERE subscribe_user_code = #{subscribeUserCode}

SELECT COUNT(*) AS subscriberStoryCount
FROM subscribe
WHERE user_code = #{userCode}

mapper.delete:
DELETE
FROM subscribe
WHERE user_code = #{userCode}
  AND subscribe_user_code = #{subscribeUserCode}

mapper.update:
UPDATE user
SET is_subscriber_visible = 'Y'
WHERE code = #{code}
