mapper.update:
UPDATE
user
SET
nickname = #{ nickname },
introduction = #{ introduction },
is_subscriber_visible = #{isSubscriberVisible}

    ,password = #{ password }


    ,profile_image = NULL


    ,profile_image = #{ profileImage }

WHERE state = 'ACTIVE'
AND user_id = #{userId}

UPDATE user
SET point = #{ point }
WHERE code = #{ code }

UPDATE user
SET revenue = #{revenue}
WHERE code = #{ code }

mapper.select:
SELECT password
FROM user
WHERE code = #{ code }

