mapper.update:
UPDATE
user
SET nickname = #{ nickname },
introduction = #{ introduction }

    ,password = #{ password }

/* 프로필이미지가 'removed'와 같을때 NULL로 */

    ,profile_image = NULL

/* 프로필이미지가 NULL */ ㅜㅇㄴ

    ,profile_image = #{ profileImage }

WHERE state = 'ACTIVE'
AND user_id = #{userId}

mapper.select:
SELECT password
FROM user
WHERE code = #{ code }
